import React, { Component } from 'react';
import AppBar from './components/AppBar';
import { createMuiTheme,MuiThemeProvider } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import { List, ListItem,Typography,IconButton,Divider } from '@material-ui/core';
import { Delete } from '@material-ui/icons';
import { Link,hashRouter as Router,Switch,Route } from 'react-router-dom';

const AppBartheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#cc0000',
			dark : '#550000',
			main : '#aa0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
	},
});

var typography = {
	width : '33%',
	margin : '0',
	float : 'left',
	opacity : '0.8',
	textAlign : 'center',
	letterSpacing : '1px'
},
iconButton = {
	color : 'crimson',
	opacity : '0.8'
},header = {
	width : '33%',
	margin : '0',
	float : 'left',
	textAlign : 'center',
	letterSpacing : '1px'
},button = {
	backgroundColor : 'green',
	letterSpacing : '1px',
	color : 'white'
}

const mainTheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#c00000',
			dark : '#500000',
			main : '#0a0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
		width : '33%',
		margin : '0',
		float : 'left',
		opacity : '0.8'
	},
	iconButton : {
		color : 'crimson',
		opacity : '0.8'
	}
});

class App extends Component {
	constructor(props){
		super(props);
		this.state = {
			contacts : [{
				name : 'Dhruvil',
				number : '8850392965'
			},{
				name : 'Rahil',
				number : '9869225071'
			},{
				name : 'Ketan',
				number : '9969326535'
			}],
		}
	}

	handleDelete = (number) => {
		console.log(number.number,this.state);
		this.setState(({ contacts }) => ({
			contacts: contacts.filter(ex => ex.number !== number.number)
		}))
	}

	render() {
		return (
			<div>
				<header>
					<MuiThemeProvider theme={AppBartheme}>
						<AppBar />
					</MuiThemeProvider>
				</header>
				<main>
					<MuiThemeProvider theme={mainTheme}>
						<Router>
							<Switch>
								<Route path = "/">
									<Link to="/add">
										<Button variant="contained" style={button} onClick = {()=>this.handleAdd()}>
											ADD
										</Button><br/>
									</Link>

									<Typography variant = 'h6' style={header}>NAME</Typography>
									<Typography variant = 'h6' style={header}>NUMBER</Typography>
									<List>
										{ this.state.contacts.map( ({name,number}) => 
											<ListItem key={number}>
												<Divider />
												<Typography variant = 'h6' style={typography}>{name}</Typography>
												<Typography variant = 'h6'style={typography}>{number}</Typography>
												{/* <ListItemSecondaryAction> */}
													<IconButton style={iconButton} onClick={()=>this.handleDelete({number})} >
														<Delete />
													</IconButton>	
												{/* </ListItemSecondaryAction> */}
											</ListItem>
										)}
									</List>
								</Route>
							</Switch>
						</Router>
					</MuiThemeProvider>
				</main>
			</div>
		);
	}

}

export default App;


