import React, { Component } from 'react';
import AppBar from './components/AppBar';
import { createMuiTheme,MuiThemeProvider } from '@material-ui/core/styles';
import { Link,HashRouter as Router,Switch,Route } from 'react-router-dom';
import Home from './home'

const AppBartheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#cc0000',
			dark : '#550000',
			main : '#aa0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
	},
});

class App extends Component {
	constructor(props){
		super(props);
		this.state = {
			contacts : [{
				name : 'Dhruvil',
				number : '8850392965'
			},{
				name : 'Rahil',
				number : '9869225071'
			},{
				name : 'Ketan',
				number : '9969326535'
			}],
		}
	}

	handleDelete = (number) => {
		console.log(number.number,this.state);
		this.setState(({ contacts }) => ({
			contacts: contacts.filter(ex => ex.number !== number.number)
		}))
	}

	render() {
		return (
			<div>
				<header>
					<MuiThemeProvider theme={AppBartheme}>
						<AppBar />
					</MuiThemeProvider>
				</header>
				<main>
					<MuiThemeProvider>
						<Router>
							<Switch>
								<Route path = "/" component={Home}/>
							</Switch>
						</Router>
					</MuiThemeProvider>
				</main>
			</div>
		);
	}

}

export default App;


