import React, { Component } from 'react';
import AppBar from './components/AppBar';
import { createMuiTheme,MuiThemeProvider } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import Contact from './components/contact';

const AppBartheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#cc0000',
			dark : '#550000',
			main : '#aa0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
	},
});

const mainTheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#c00000',
			dark : '#550000',
			main : '#aa0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
	},
});

class App extends Component {
	constructor(props){
		super(props);
		this.state = {
			contacts : [{
				name : 'Dhruvil',
				number : '8850392965'
			},{
				name : 'Rahil',
				number : '9869225071'
			},{
				name : 'Khushali',
				number : '9699580280'
			}],
		}
	}

	render() {
		return (
			<div>
				<header>
					<MuiThemeProvider theme={AppBartheme}>
						<AppBar />
					</MuiThemeProvider>
				</header>
				<main>
				<MuiThemeProvider theme={mainTheme}>
					<Button >ADD</Button>
					<Contact  name = {this.state.contacts[0].name} number = {this.state.contacts[0].number} />
				</MuiThemeProvider>
				</main>
			</div>
		);
	}
}

export default App;


