import React, { Component } from 'react';
import AppBar from './components/AppBar';
import { createMuiTheme,MuiThemeProvider } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import Contact from './components/contact';
import { List, ListItem } from '@material-ui/core';

const AppBartheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#cc0000',
			dark : '#550000',
			main : '#aa0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
	},
});

const mainTheme = createMuiTheme({
	palette : {
		primary : {
			light : '#555555',
			dark : '#111111',
			main : '#333333',
			contrastText : '#ffffff'
		},
		secondary : {
			light : '#ffffff',
			dark : '#dddddd',
			main : '#eeeeee',
			contrastText : '#111111'
		},
		error : {
			light : '#c00000',
			dark : '#500000',
			main : '#0a0000',
			contrastText : '#00ffff'
		}
	},
	typography: {
		useNextVariants: true,
		width : '33%',
		margin : '0',
		float : 'left',
		opacity : '0.8'
	},
	iconButton : {
		color : 'crimson',
		opacity : '0.8'
	}
});

class App extends Component {
	constructor(props){
		super(props);
		this.state = {
			contacts : [{
				name: 'NAME',
				number : 'NUMBER'
			},{
				name : 'Dhruvil',
				number : '8850392965'
			},{
				name : 'Rahil',
				number : '9869225071'
			},{
				name : 'Khushali',
				number : '9699580280'
			}],
		}
	}

	render() {
		return (
			<div>
				<header>
					<MuiThemeProvider theme={AppBartheme}>
						<AppBar />
					</MuiThemeProvider>
				</header>
				<main>
					<MuiThemeProvider theme={mainTheme}>
							<Button >ADD</Button>
							<List>
								{ this.state.contacts.map( ({name,number}) => 
									<ListItem key={number}>
										<Contact name={name} number={number} buttonClick={() => handleDelete({number})}/>
									</ListItem>
								)}
							</List>
					</MuiThemeProvider>
				</main>
			</div>
		);
	};

	handleDelete = (number) = {}
}

export default App;


